/*==================================================
AllStudentsView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the all students view page.
================================================== */
import React from 'react'; 
import { Link } from "react-router-dom";
import './styles/AllStudentsView.css';

const AllStudentsView = (props) => {
  const {students, deleteStudent} = props;

  // If there is no student, display a message
  if (!students.length) {
    return (
      <div className="empty-state">
        <div className="empty-icon">üë®‚Äçüéì</div>
        <h1>All Students</h1>
        <p>There are no students in the database.</p>
        <Link to={`/newstudent`} className="add-button">
          + Add Your First Student
        </Link>
      </div>
    );
  }

  // If there is at least one student, render All Students view 
  return (
    <div className="students-container">
      <div className="header-section">
        <div className="title-section">
          <h1>All Students</h1>
          <p className="subtitle">Manage and view all student records</p>
        </div>
        <Link to={`/newstudent`} className="add-button">
          + Add New Student
        </Link>
      </div>

      <div className="students-grid">
        {students.map((student) => (
          <div key={student.id} className="student-card">
            <div className="student-image">
              <img 
                src={student.imageUrl || 'https://via.placeholder.com/150?text=Student'} 
                alt={`${student.firstname} ${student.lastname}`}
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/150?text=Student';
                }}
              />
            </div>
            <div className="student-content">
              <Link to={`/student/${student.id}`} className="student-name">
                <h2>{student.firstname} {student.lastname}</h2>
              </Link>
              <div className="student-details">
                <p className="student-email">
                  <span className="icon">üìß</span> {student.email}
                </p>
                {student.gpa && (
                  <p className="student-gpa">
                    <span className="icon">üìö</span> GPA: {student.gpa}
                  </p>
                )}
                {student.campus && (
                  <p className="student-campus">
                    <span className="icon">üèõÔ∏è</span> {student.campus.name}
                  </p>
                )}
              </div>

              <div className="action-buttons">
                <Link 
                  to={`/student/${student.id}`} 
                  className="view-button"
                >
                  View Details
                </Link>
                <button 
                  className="delete-button"
                  onClick={() => {
                    if (window.confirm('Are you sure you want to delete this student?')) {
                      deleteStudent(student.id);
                    }
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllStudentsView;
