/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
import React from 'react'; 
import { Link } from 'react-router-dom';
import './styles/Student.css';

const StudentView = (props) => {
  const { student, handleDelete } = props;

  // Loading state
  if (!student) {
    return <div>Loading...</div>;
  }

  // Helper function to format GPA
  const formatGPA = (gpa) => {
    if (gpa === null || gpa === undefined) return 'Not Available';
    return typeof gpa === 'number' ? gpa.toFixed(2) : gpa;
  };

  return (
    <div className="student-container">
      <div className="student-header">
        <h1>{student.firstname} {student.lastname}</h1>
        <div className="student-actions">
          <Link to={`/student/${student.id}/edit`} className="action-button edit">
            Edit
          </Link>
          <button onClick={handleDelete} className="action-button delete">
            Delete
          </button>
        </div>
      </div>

      <div className="student-details">
        <div className="student-image-container">
          <img 
            src={student.imageUrl || 'https://via.placeholder.com/150?text=Student'} 
            alt={`${student.firstname} ${student.lastname}`}
            className="student-image"
            onError={(e) => {
              e.target.src = 'https://via.placeholder.com/150?text=Student';
            }}
          />
        </div>

        <div className="student-info">
          <p><strong>Email:</strong> {student.email}</p>
          <p><strong>GPA:</strong> {formatGPA(student.gpa)}</p>
        </div>

        <div className="campus-section">
          <h3>Campus Information</h3>
          <p>Currently enrolled at:</p>
          {student.campus ? (
            <Link to={`/campus/${student.campus.id}`} className="campus-link">
              {student.campus.name}
            </Link>
          ) : (
            <p className="not-enrolled">Not currently enrolled</p>
          )}
        </div>

        <div className="back-button-container">
          <Link to="/students" className="action-button back">
            ‚Üê Back to All Students
          </Link>
        </div>
      </div>
    </div>
  );
};

export default StudentView;
