import React from 'react';
import { Link } from 'react-router-dom';
import './styles/EditCampus.css';

const EditCampusView = ({ handleSubmit, handleChange, formData, errors }) => {
  return (
    <div className="edit-campus-container">
      <h1>Edit Campus</h1>

      <form onSubmit={handleSubmit} className="edit-form">
        <div className="form-group">
          <label>Campus Name<span className="required">*</span></label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={errors.name ? "error" : ""}
          />
          {errors.name && <span className="error-message">{errors.name}</span>}
        </div>

        <div className="form-group">
          <label>Address<span className="required">*</span></label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className={errors.address ? "error" : ""}
          />
          {errors.address && <span className="error-message">{errors.address}</span>}
        </div>

        <div className="form-group">
          <label>Description<span className="required">*</span></label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            className={errors.description ? "error" : ""}
            rows="4"
          />
          {errors.description && <span className="error-message">{errors.description}</span>}
        </div>

        <div className="form-group">
          <label>Image URL</label>
          <input
            type="url"
            name="imageURL"
            value={formData.imageURL}
            onChange={handleChange}
            className={errors.imageURL ? "error" : ""}
            placeholder="https://example.com/image.jpg"
          />
          {errors.imageURL && <span className="error-message">{errors.imageURL}</span>}
          {formData.imageURL && !errors.imageURL && (
            <img 
              src={formData.imageURL} 
              alt="Preview" 
              className="image-preview"
              onError={(e) => {
                e.target.style.display = 'none';
                errors.imageURL = "Failed to load image";
              }}
            />
          )}
        </div>

        {errors.submit && <div className="submit-error">{errors.submit}</div>}

        <div className="form-actions">
          <button type="submit" className="submit-button">
            Save Changes
          </button>
          <Link to={`/campus/${formData.id}`} className="cancel-button">
            Cancel
          </Link>
        </div>
      </form>
    </div>
  );
};

export default EditCampusView;
