/*==================================================
/src/components/containers/NewCampusContainer.js

The Container component is responsible for stateful logic and data fetching, and
passes data (if any) as props to the corresponding View component.
If needed, it also defines the component's "connect" function.
================================================== */
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { addCampusThunk } from '../../store/thunks';
import Header from './Header';
import { NewCampusView } from '../views';

class NewCampusContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      address: "",
      description: "",
      imageURL: "",
      errors: {}
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  validateForm = () => {
    const errors = {};
    if (!this.state.name) errors.name = "Name is required";
    if (!this.state.address) errors.address = "Address is required";
    return {
      errors,
      isValid: Object.keys(errors).length === 0
    };
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const { errors, isValid } = this.validateForm();

    if (!isValid) {
      this.setState({ errors });
      return;
    }

    try {
      const campusData = {
        name: this.state.name,
        address: this.state.address,
        description: this.state.description,
        imageURL: this.state.imageURL
      };

      console.log("Submitting campus data:", campusData);
      await this.props.addCampus(campusData);
      this.props.history.push('/campuses');
    } catch (err) {
      console.error(err);
      this.setState({
        errors: {
          ...this.state.errors,
          submit: "Failed to add campus. Please try again."
        }
      });
    }
  }

  render() {
    return (
      <div>
        <Header />
        <NewCampusView
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
          errors={this.state.errors}
        />
      </div>
    );
  }
}

const mapDispatch = (dispatch) => {
  return {
    addCampus: (campus) => dispatch(addCampusThunk(campus))
  };
};

export default withRouter(connect(null, mapDispatch)(NewCampusContainer));
