/*==================================================
/src/components/containers/NewCampusContainer.js

The Container component is responsible for stateful logic and data fetching, and
passes data (if any) as props to the corresponding View component.
If needed, it also defines the component's "connect" function.
================================================== */
import React from 'react'; 
import { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { addCampusThunk } from '../../store/thunks';
import Header from './Header';
import { NewCampusView } from '../views';

class NewCampusContainer extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit = async (campus) => {
    try {
      console.log("NewCampusContainer - Submitting campus:", campus); // Debug log

      // Make sure all required fields are present
      const campusData = {
        name: campus.name,
        address: campus.address,
        description: campus.description || '',
        imageURL: campus.imageURL || null
      };

      console.log("NewCampusContainer - Formatted data:", campusData); // Debug log

      await this.props.addCampus(campusData);
      console.log("NewCampusContainer - Campus added successfully"); // Debug log

      this.props.history.push('/campuses');
    } catch (err) {
      console.error("NewCampusContainer - Error:", err);
    }
  }

  render() {
    return (
      <div>
        <Header />
        <NewCampusView handleSubmit={this.handleSubmit} />
      </div>
    );
  }
}

const mapDispatch = (dispatch) => {
  return {
    addCampus: (campus) => dispatch(addCampusThunk(campus))
  };
};

export default withRouter(connect(null, mapDispatch)(NewCampusContainer));
