/*==================================================
NewCampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the new campus page.
================================================== */
import React from 'react';
import { Link } from 'react-router-dom';
import './styles/NewCampusView.css';

const NewCampusView = ({ handleSubmit, handleChange, formData }) => {
  const { name, address, description, imageURL, error, isSubmitting } = formData;

  return (
    <div className="new-campus-container">
      <div className="header-section">
        <h1>Add New Campus</h1>
        <Link to="/campuses" className="back-button">
          Back to Campuses
        </Link>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="campus-form">
        <div className="form-group">
          <label>Campus Name *</label>
          <input
            type="text"
            name="name"
            value={name}
            onChange={handleChange}
            placeholder="Enter campus name"
            required
          />
        </div>

        <div className="form-group">
          <label>Address *</label>
          <input
            type="text"
            name="address"
            value={address}
            onChange={handleChange}
            placeholder="Enter campus address"
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            value={description}
            onChange={handleChange}
            placeholder="Enter campus description"
            rows="4"
          />
        </div>

        <div className="form-group">
          <label>Image URL</label>
          <input
            type="url"
            name="imageURL"
            value={imageURL}
            onChange={handleChange}
            placeholder="https://example.com/image.jpg"
          />
        </div>

        <button 
          type="submit" 
          className={`submit-button ${isSubmitting ? 'loading' : ''}`}
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Adding...' : 'Add Campus'}
        </button>
      </form>
    </div>
  );
};

export default NewCampusView;
